CREATE TABLE `erp_items` ( `id` INT NOT NULL AUTO_INCREMENT , `company` INT(100) NOT NULL , `item_category` INT NOT NULL , `item_unit` INT NOT NULL , `item_code` VARCHAR(100) NOT NULL , `item_name` VARCHAR(100) NOT NULL , `generic_name` VARCHAR(100) NOT NULL , `description` TEXT NOT NULL , `image` VARCHAR(100) NOT NULL , `created` DATETIME NOT NULL , `updated` DATETIME NOT NULL , `status` TINYINT NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
